#!usr/bin/perl -w
use strict;

#assumes your running this from the directory where the script lives. If not, modify the paths of the input sequences below.
#input: protein fasta file
my $pep = "../OGS_v1_1/preprocessing/from_dan_11-25-2014/agla_v1_1.faa";
my $cdna = "../OGS_v1_1/preprocessing/from_dan_11-25-2014/agla_v1_1.fna";
