#cds                                                                                                               
 #substitute seq 1 -AGLA025105-RA                                                                                            
print `sed '' $cds > temp_cds.fasta`;

#substitute seq 2 - AGLA006649-RA                                                                                             
print `sed '' temp_cds.fasta > temp_cds2.fasta`;

#substitute seq 3 AGLA012060-RA                                                                                                
print `sed '' temp_cds2.fasta > $cds_out`;

print `rm temp_cds*.fasta`;;
